import React from 'react';
import {
    View,
    Text,
    Image,
    TouchableOpacity
} from 'react-native';

import {
    createDrawerNavigator,
    DrawerContentScrollView
} from "@react-navigation/drawer";

import { MainLayout } from "../screens";
import {
    COLORS,
    FONTS,
    SIZES,
    constants,
    icons,
    dummyData
} from "../constants";


const Drawer = createDrawerNavigator()

const CustomDrawerItem = ({ label, icon }) => {

    return (
        <TouchableOpacity
            style={{
                flexDirection: 'row',
                height: 40,
                marginBottom: SIZES.base,
                paddingLeft: SIZES.radius,
                borderRadius: SIZES.base
                //background color 
            }}
        //onPress
        >
            <Image
                source={icon}
                style={{
                    width: 20,
                    height: 20,
                    tintColor: COLORS.green
                }}
            />
            <Text
                style={{
                    marginLeft: 15,
                    color: COLORS.green,
                    ...FONTS.h3

                }}
            >{label}</Text>
        </TouchableOpacity>
    )
}

const CustomDrawerContent = ({ navigation }) => {
    return (
        <DrawerContentScrollView
            scrollEnabled={true}
            contentContainerStyle={{ flex: 1 }}
        >
            <View
                style={{
                    flex: 1,
                    paddingHorizontal: SIZES.radius
                }}
            >
            </View>
            {/*Close*/}
            <View
                style={{
                    alignItems: 'flex-start',
                    justifyContent: 'center'


                }}
            >
                <TouchableOpacity
                    style={{
                        alignItems: 'center',
                        justifyContent: 'center'
                    }}
                    onPress={() => navigation.closeDrawer()}
                >
                    <Image
                        source={icons.cross}
                        style={{
                            height: 35,
                            width: 35,
                            tintColor: COLORS.green
                        }}
                    />
                </TouchableOpacity>
                {/*Profile*/}

                <TouchableOpacity
                    style={{
                        flexDirection: 'row',
                        marginTop: SIZES.radius,
                        alignItems: 'center'
                    }}
                    onPress={() => console.log("Profile")}
                >
                    <Image
                        source={dummyData.myProfile?.profile_image}
                        style={{
                            width: 50,
                            height: 50,
                            borderRadius: SIZES.radius
                        }}

                    />

                    <View
                        style={{
                            marginLeft: SIZES.radius

                        }}
                    >

                        <Text
                            style={{
                                color: COLORS.green,
                                ...FONTS.h3
                            }}
                        >{dummyData.myProfile?.name}
                        </Text>

                        <Text
                            style={{
                                color: COLORS.green,
                                ...FONTS.body4
                            }}
                        >
                            View your Profile
                        </Text>

                    </View>
                </TouchableOpacity>
                {/*Drawer Items*/}
                <View
                    style={{
                        flex: 1,
                        marginTop: SIZES.padding
                    }}
                >
                    <CustomDrawerItem
                        label={constants.screens.home}
                        icon={icons.home}
                    />

                    <CustomDrawerItem
                        label={constants.screens.my_wallet}
                        icon={icons.wallet}
                    />

                    <CustomDrawerItem
                        label={constants.screens.notification}
                        icon={icons.notification}
                    />

                    <CustomDrawerItem
                        label={constants.screens.favourite}
                        icon={icons.favourite}
                    />

                    <View
                        style={{
                            height: 1,
                            marginVertical: SIZES.radius,
                            marginLeft: SIZES.radius,
                            backgroundColor: COLORS.lightGray1
                        }}
                    />
                    <CustomDrawerItem
                        label="Track your Order"
                        icon={icons.location}
                    />

                    <CustomDrawerItem
                        label="Coupons"
                        icon={icons.coupon}
                    />

                    <CustomDrawerItem
                        label={constants.screens.cart}
                        icon={icons.cart}
                    />

                    <CustomDrawerItem
                        label="Settings"
                        icon={icons.setting}
                    />

                    <CustomDrawerItem
                        label="Invite a Friend"
                        icon={icons.profile}
                    />

                    <CustomDrawerItem
                        label="Help Center"
                        icon={icons.help}
                    />

                </View>
            </View>
        </DrawerContentScrollView >
    )
}

const CustomDrawer = () => {
    return (
        <View
            style={{
                flex: 1,
                backgroundColor: COLORS.green

            }}
        >
            <Drawer.Navigator
                drawerType="slide"
                overlayColor="transparent"
                drawerStyle={{
                    flex: 1,
                    width: '65%',
                    paddingRight: 20,
                    backgroundColor: 'transparent'
                }}
                sceneContainerStyle={{
                    backgroundColor: 'transparent'

                }}
                initialRouteName="MainLayout"
                drawerContent={props => {
                    return (
                        <CustomDrawerContent
                            navigation={props.navigation}
                        />
                    )
                }}

            >
                <Drawer.Screen name="Main Layout">
                    {props => <MainLayout {...props} />}
                </Drawer.Screen>


            </Drawer.Navigator>
        </View >
    )
}

export default CustomDrawer;
